import {
  all,
  takeLatest,
  takeEvery,
  call,
  put,
  take,
  delay,
} from "redux-saga/effects";
import { LOG_IN, LOG_IN_SUCCESS, LOG_IN_FAILURE } from "../reducers/user";

const HELLO_SAGA = "HELLO_SAGA";

function loginAPI() {
  // 서버에 요청을 보내는 부분
}

function* login() {
  try {
    yield call(loginAPI);
    // put은 dispatch와 동일
    yield put({
      type: LOG_IN_SUCCESS,
    });
  } catch (e) {
    // loginAPI 실패시
    console.error(e);
    yield put({
      type: LOG_IN_FAILURE,
    });
  }
}

function* watchLogin() {
  yield takeEvery(LOG_IN, login);
}

function* hello(){
  yield delay(1000)
}

// 1. login 요청을 해서 상단의 login함수(LOG_IN액션 실행)
// 실행되게 함(비동기동작 위한 대기) 만약 그러면 LOG_IN_SUCCESS 실행
// 반복문 넣는 이유: 예를들어 빼면 로그아웃후 다시 로그인시 작동불가 방지

// function* watchLogin() {
//   while (true) {
//     yield take(LOG_IN);
//     yield delay(2000);
//     // put: saga의 dispatch
//     put({
//       type: LOG_IN_SUCCESS,
//     });
//   }
// }
//연속으로 값 막 누르면 마지막것만 적용해서 유효하게 하겠다면 takeLatest
//매번 연결시키려면 (while(true))처럼 -> takeevery
//로그인버튼 두번클릭시 실수니깐(takelatest로 쓰는게 나을듯)
//반면 카운트같은 경우 모든클릭 유효하니 takeevery를 쓰는것
function* watchHello() {
  yield takeLatest(HELLO_SAGA, hello);
}

// function* watchHello() { 상단함수와 비교(둘다 같음)
//   while (true) {
//     yield take(HELLO_SAGA);
//     console.log(1);
//     console.log(2);
//     console.log(3);
//     console.log(4);
//   }
// }

// take: 해당 액션이 dispatch되면 제너레이터를 next하는 이펙트
// HELLO_SAGA가 불려오면 next를 해준다.(hello saga까지)
// 하지만 while 하면 무제한 반복(pages/index.js)에 dispatch 많이 복사하면 그 여러개 실행
// 그리고 for을 통해 횟수를 조절할 수도 있음
// function* helloSaga() {
//   console.log("before saga");
//   // for (let i = 0; i < 5; i++) {
//   yield take(HELLO_SAGA);
//   console.log("hello saga");
//   // }
// }

export default function* userSaga() {
  yield all[(watchLogin(), watchHello())];
}
