// 타임라인 역할 페이지

import React, { useEffect } from "react";
import PostForm from "../components/PostForm";
import PostCard from "../components/PostCard";
import { useDispatch, useSelector } from "react-redux";
import { LOG_IN } from "../reducers/user";

const dummy = {
  isLoggedIn: true,
  imagePaths: [],
  mainPosts: [
    {
      User: {
        id: 1,
        nickname: "전세환",
      },
      content: "첫 번째 게시글",
      img:
        "https://m.post.naver.com/viewer/postView.nhn?volumeNo=27220260&memberNo=3613482#",
    },
  ],
};

const Home = () => {
  const dispatch = useDispatch();
  const { isLoggedIn, user } = useSelector(state => state.user); // 여기서 state는 전체 state를 가르킨다. ,
  console.log(user);
  useEffect(() => {
    dispatch({ type: "LOG_IN", data: { nickname: "제로초" } });
  }, []);

  return (
    <div>
      {dummy.isLoggedIn && <PostForm />}
      {/* 게시글 나오는 공간 */}
      {dummy.mainPosts.map(c => {
        return <PostCard key={c} post={c} />;
      })}
    </div>
  );
};
export default Home;
