// 초기값(추후 dummy값을 이쪽으로 옮겨야 함)
const dummyUser = {
  nickname: "제로초",
  Post: [],
  Followings: [],
  Followers: [],
  signUpData: {
    id:'',
    nickname:'',
    password:'',

  },
};

export const initialState = {
  isLoggedIn: false,
  user: null,
};

//action명
export const LOG_IN = "LOG_IN";
export const LOG_OUT = "LOG_OUT";
export const SIGN_UP = "SIGN_UP";

//action 정의
export const loginAction = {
  type: LOG_IN,
};

export const logoutAction = {
  type: LOG_OUT,
};

// 동적데이터는 함수를 통해 넣어야 함
export const signUpAction = data => {
  return {
    type: SIGN_UP,
    data: data,
  };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case LOG_IN: {
      return {
        ...state,
        isLoggedIn: true,
        user: dummyUser,
      };
    }
    case LOG_OUT: {
      return {
        ...state,
        isLoggedIn: false,
        user: null,
      };
    }
    case SIGN_UP: {
      return {
        ...state,
        signUpData: action.data,
      };
    }
    default: {
      return {
        ...state,
      };
    }
  }
};

export default reducer;
