import { all, takeLatest, call, put, take } from "redux-saga/effects";
import { LOG_IN, LOG_IN_SUCCESS, LOG_IN_FAILURE } from "../reducers/user";

const HELLO_SAGA = "HELLO_SAGA";

function loginAPI() {
  // 서버에 요청을 보내는 부분
}

function* login() {
  try {
    yield call(loginAPI);
    // put은 dispatch와 동일
    yield put({
      type: LOG_IN_SUCCESS,
    });
  } catch (e) {
    // loginAPI 실패시
    console.error(e);
    yield put({
      type: LOG_IN_FAILURE,
    });
  }
}

// 1. login 요청을 해서 상단의 login함수 실행되게 함(비동기동작 위한 대기)

function* watchLogin() {
  yield take(LOG_IN);
  // put: saga의 dispatch
  put({
    type: LOG_IN_SUCCESS,
  });
}

function* watchSignUp() {}

// take: 해당 액션이 dispatch되면 제너레이터를 next하는 이펙트
// HELLO_SAGA가 불려오면 next를 해준다.(hello saga까지)
// 하지만 while 하면 무제한 반복(pages/index.js)에 dispatch 많이 복사하면 그 여러개 실행
// 그리고 for을 통해 횟수를 조절할 수도 있음
// function* helloSaga() {
//   console.log("before saga");
//   // for (let i = 0; i < 5; i++) {
//   yield take(HELLO_SAGA);
//   console.log("hello saga");
//   // }
// }

export default function* userSaga() {
  yield all[(watchHello(), watchLogin(), watchSignUp())];
}
